function expandzzz(d){   
    var children = (d.children)?d.children:d._children;
    if (d._children) {        
        d.children = d._children;
        d._children = null;       
    }
    if(children)
      children.forEach(expandzzz);
}

function expandAll(){
    expandzzz(nodes); 
   // update(nodes);
}

